mapper.select:
SELECT id, name, description, user_id, created_at, updated_at
FROM rooms
WHERE id = #{id}

SELECT id, name, description, user_id, created_at, updated_at
FROM rooms
WHERE user_id = #{userId}

mapper.update:
UPDATE rooms
SET name = #{name},
    description = #{description},
    user_id = #{userId},
    updated_at = #{updatedAt}
WHERE id = #{id}
